import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';


// import { xxxXxxxXxxx } from '../../actions';



class AdminAddCat extends Component {

    state = {
        catdata: {
            title: '',
            description: ''
        },
        subcatdata: [{
                title: '',
                description: ''
            }]
    }

    componentDidMount() {
        // this.props.dispatch(xxxXxxxXxxx());
    }


    componentDidUpdate(prevProps, prevState) {
    }



    addDefaultImg = (ev) => {
        const newImg = '/images/default/default.jpg';
        if (ev.target.src !== newImg) {
            ev.target.src = newImg
        }  
    }


    render() {
        return (
            <div className="admin">
                    <div>
                        <form onSubmit={this.submitForm}>
                            <table>
                            <tbody>
                                <tr>
                                    <td>
                                        <h3>Title</h3>
                                    </td>
                                    <td>

                                        <input
                                            type="text"
                                            placeholder={null}
                                            defaultValue={null} 
                                            onChange={(event) => this.handleInput(event)}
                                        />




                                    </td>
                                </tr>


                                <tr>
                                    <td>
                                        <h3>Description</h3>
                                    </td>
                                    <td>
                                        <textarea
                                            type="text"
                                            placeholder="Enter category description"
                                            defaultValue={null} 
                                            onChange={(event) => this.handleInput(event)}
                                            rows={6}
                                        />
                                    </td>
                                </tr>


                                <tr>
                                    <td>
                                        <img className="change_cat_img" src={`/images/cover_img_cat/XXXX.jpg`} onError={this.addDefaultImg}/>
                                    </td>
                                    <td>
                                        <div className="form_element">
                                            <input type="file" className="form-control" multiple name="file" onChange={this.onChangeHandler}/>
                                            <br />
                                            <button type="button" className="btn btn-success btn-block" onClick={this.onClickHandler}>Upload</button> 
                                        </div>
                                    </td>
                                </tr>

                                <tr>
                                    <td>
                                        <h3>Sub-categories</h3>
                                    </td>
                                </tr>




                                <tr >
                                    <td>

                                        <input
                                            type="text"
                                            placeholder="Add subcategory"
                                            defaultValue={null} 
                                            onChange={(event) => this.handleInput(event)}
                                        />
                                    </td>

                                    <td>
                                        <input
                                            type="text"
                                            placeholder="Description"
                                            defaultValue={null} 
                                            onChange={(event) => this.handleInput(event)}
                                        />
                                    </td>
                                </tr>

                                <tr className="spacer"></tr>

                                <tr>
                                    <td colSpan="2">
                                        <button type="button" 
                                            className="delete" 
                                            onClick={(e) => { if (window.confirm('Are you sure you wish to delete this category?')) this.deleteCat(e, 'some ID') } }
                                        >
                                            Delete Category
                                        </button>

                                    </td>
                                </tr>

                                <tr className="spacer"></tr>

                                <tr>
                                    <td>
                                        <button type="submit">Save Changes</button>
                                    </td>

                                    <td>
                                        <button type="button" onClick={this.cancel}>Cancel</button>
                                    </td>

                                </tr>

                                <tr className="spacer"></tr>


                            </tbody>
                            </table>

                        </form>

                    </div>
            </div>
        );
    }
}


function mapStateToProps(state) {
    // console.log(state);

    return {
        // xxx: state.xxx.xxxx

    }
}


export default withRouter(connect(mapStateToProps)(AdminAddCat));
