import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';


import { getInfoText, updateInfoText } from '../../actions';



class AdminInfo extends Component {


    state= {
    }

    componentDidMount() {
        this.props.dispatch(getInfoText());

    }


    componentDidUpdate(prevProps, prevState) {
    }


    render() {

        console.log(this.props)



        return (



            <div className="admin">
                <div className="admin_intro">

                    <h1>Change the info about the info page</h1>


                    <form onSubmit={this.submitForm}>
                        <table>
                            <tbody>







                            <p>
                                <b>Info Page</b> 
                            </p>
                            <input
                                type="text"
                                placeholder="Paragraph 1 Heading"
                                defaultValue={null} 
                                onChange={(event) => this.handleInput(event)}
                            />
                            <br />
                            <textarea
                                type="text"
                                placeholder="Paragraph 1 Content"
                                defaultValue={null} 
                                onChange={(event) => this.handleInput(event)}
                                rows={6}
                            />

                            <p>Paragraph 1 Image</p>

                            <input
                                type="text"
                                placeholder="Paragraph 2 Heading"
                                defaultValue={null} 
                                onChange={(event) => this.handleInput(event)}
                            />
                            <br />
                            <textarea
                                type="text"
                                placeholder="Paragraph 2 Content"
                                defaultValue={null} 
                                onChange={(event) => this.handleInput(event)}
                                rows={6}
                            />
                            <p>Paragraph 2 Image</p>



                        </tbody>
                        </table>
                    </form>


                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    // console.log(state);

    return {
        infotext: state.infos.text

    }
}


export default connect(mapStateToProps)(AdminInfo)
