import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';


// COMPONENTS

import Header from './header';
import NewsList from './news_list';


class Search extends Component {

  state = {
    items: [],
    filtered: []
  }

  getKeyword = (event) => {
    // console.log(event.target.value);
    let keyword = event.target.value;
    let filtered = this.state.items.filter( (item) => {
      return item.title.indexOf(keyword) > -1
    });
    this.setState({
      filtered
    })
    // console.log(filtered);
  }



// ************************************************************ FETCH JSON DATA **********************************************************************


  componentDidMount() {
    this.getItems();

  }

  getItems = _ => {
    axios.get('http://localhost:4000/items')

      // .then(response => response.json())

      .then(response => console.log(Array.isArray(response.data.data)))

      // .then(({ data }) => {
      //   console.log(data)
      // })

      .then(response => this.setState({ items: response.data.data}))
      .then(response => console.log(response.data))
      .catch(err => console.error(err))
  }


// ***************************************************************************************************




  render() {

    let newsFiltered = this.state.filtered;
    let newsWhole = this.state.items;

    return (

      <div>
        <Header keywords={this.getKeyword} />

        {/* pass the state to the props of the component  */}
        <NewsList news={this.state.filtered.length === 0 ? newsWhole : newsFiltered}>
          <h3>
            Items: 
          </h3>
        </NewsList>
      </div>

    )
  }


}

ReactDOM.render(<App />, document.querySelector('#root'));
