import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';


import { getCats, getItemsByCat } from '../../actions';
import CatItem from './cats_list_item';





class CatList extends Component {

    catId = this.props.catInfo.cat_id;

    componentDidMount() {
        this.props.dispatch(getCats());
        // this.props.dispatch(getItemsByCat(this.props.catInfo.cat_id));

    }

    // renderCatItem = (catId) => {

    // }


    render() {



        // console.log(cats);
        let cats = this.props.cats;

        return (
            cats ?
                cats.map( (cat, i) => (
                        <Link key={cat._id} to={`/category/${cat.cat_id}`}>
                            <h2>{cat.title}</h2><br/>
                            {/* {renderCatItem(cat.cat_id)} */}
                            <CatItem catId={catId} catInfo={this.props.cats} catItems={this.props.items}/>
                        </Link>
                ))
            : null



        );
    }
}

function mapStateToProps(state) {
    // console.log(state);
    return {
        cats: state.cats.cats,
        items: state.cats.catitems
    }
}


export default connect(mapStateToProps)(CatList)
