import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';


import { getAllColls, getAllCats, getAllSubCats  } from '../../actions';

import { addCat, deleteCat, addSubcat, deleteSubcat, updateCat, updateSubcat  }  from '../../actions';

class AdminCat extends Component {

    chosenCatInfo = this.state.chosenCatInfo;

    state = {
        cat: {
            _id: chosenCatInfo._id,
            title: chosenCatInfo.title,
            description: chosenCatInfo.description

        }
    }

    componentDidMount() {
        this.props.dispatch(getAllColls())
        this.props.dispatch(getAllCats());
        this.props.dispatch(getAllSubCats());
    }

    render() {


        console.log(this.state)
        return (
            <div>
                { this.props.chosenCatInfo ? 
                    <div>
                        <form onSubmit={this.submitForm}>
                            <table>
                            <tbody>
                                <tr>
                                    <td>
                                        <h2>Title</h2>
                                    </td>
                                    <td>

                                        <input
                                            type="text"
                                            placeholder={this.props.chosenCatInfo.title}
                                            defaultValue={this.props.chosenCatInfo.title} 
                                            onChange={(event) => this.handleInput(event)}
                                        />




                                    </td>
                                </tr>


                                <tr>
                                    <td>
                                        <h2>Description</h2>
                                    </td>
                                    <td>
                                        <textarea
                                            type="text"
                                            placeholder="Enter category description"
                                            defaultValue={null} 
                                            onChange={(event) => this.handleInput(event)}
                                            rows={6}
                                        />
                                    </td>
                                </tr>


                                <tr>
                                    <td>
                                        <img className="change_cat_img" src={`/images/cover_img_cat/${this.props.chosenCatInfo._id}.jpg`}  />
                                    </td>
                                    <td>
                                        Change image
                                    </td>
                                </tr>

                                <tr>
                                    <td>
                                        <h3>Sub-categories</h3>
                                    </td>
                                </tr>


                                { this.props.subcats ?
                                    this.props.subcats.map( (subcat, i) => (

                                                subcat.parent_cat == this.props.chosenCatInfo._id ? 
                                                    <tr key={i}>
                                                        <td>

                                                            <input
                                                                type="text"
                                                                placeholder={subcat.title}
                                                                defaultValue={subcat.title} 
                                                                onChange={(event) => this.handleInput(event)}
                                                            />




                                                        </td>

                                                        <td>
                                                            <input
                                                                type="text"
                                                                placeholder="Description"
                                                                defaultValue={subcat.description} 
                                                                onChange={(event) => this.handleInput(event)}
                                                            />
                                                        </td>
                                                        <td>
                                                            <button type="button">Remove</button>

                                                        </td>
                                                    </tr>

                                                : null 


                                    ) )    
                                : null }
                                <tr >
                                                        <td>

                                                            <input
                                                                type="text"
                                                                placeholder="Add new subcategory"
                                                                defaultValue={null} 
                                                                onChange={(event) => this.handleInput(event)}
                                                            />




                                                        </td>

                                                        <td>
                                                            <input
                                                                type="text"
                                                                placeholder="Description"
                                                                defaultValue={null} 
                                                                onChange={(event) => this.handleInput(event)}
                                                            />
                                                        </td>
                                                        <td>
                                                            <button type="button">+</button>

                                                        </td>
                                                    </tr>
                            </tbody>
                            </table>
                        </form>
                        <button type="button">Delete Category</button>
                        <button type="submit">Save</button>
                    </div>
                : null }
            </div>



        )
    }
}

function mapStateToProps(state) {
    return {
        colls:state.collections.colls,
        cats:state.cats.cats,
        subcats:state.cats.subcats
    }
}

export default connect(mapStateToProps)(AdminCat);
