export default function(state={}, action) {
    switch(action.type) {

        case 'GET_ALL_CATS':
            return { 
                ...state, 
                cats:action.payload 
            };
        case 'GET_ITEMS_BY_CAT':
            return { 
                ...state, 
                catitems:action.payload 
            };


        default:
            return state;
    }
}
