import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';




import { getItemById, updateItem, clearItem, deleteItem } from '../../actions';



class ChapterIndex extends PureComponent {


    state = {
        formdata: {
            _id: this.props.match.params.id,
            pdf_page_index: []

        },

        saved: false


    }


    // pdf_page_index: [
    //     {
    //         page: Number,
    //         heading: String,
    //         description: String
    //     }
    // ]


    componentDidMount() {
        this.props.dispatch(getItemById(this.props.match.params.id))

    }





    componentDidUpdate(prevProps, prevState) {

        if (this.props !== prevProps) {
            if (this.props.items.item.pdf_page_index) {

                let newFormdata = {
                    ...this.state.formdata,
                    pdf_page_index: this.props.items.item.pdf_page_index
                }

                this.setState({
                    formdata: newFormdata
                })  
            }


        } 


    }




    addField = () => {
        let newFormdata = {
            ...this.state.formdata,
            pdf_page_index: [
                ...this.state.formdata.pdf_page_index,
                {
                    description: "",
                    heading: "",
                    page: null
                }
            ]

        }
        this.setState({
            formdata: newFormdata
        }) 

    }

    onSubmit = (e) => {
        e.preventDefault();

        this.props.dispatch(updateItem(
            { ...this.state.dataToUpdate }
        ))




        this.setState({
            saved: true
        })

        setTimeout(() => {
            this.props.history.push(`/user/edit-item-file/${this.props.match.params.id }`)
        }, 1000)

    }


    renderChapters = () => {
                // console.log(this.props.items.item.pdf_page_index)
                return (
                        this.props.items.item.pdf_page_index.map( (chapt, i) => (


                            <tr key={i}>
                                <td>
                                    <input
                                        type="number"
                                        placeholder="Page Number"
                                        defaultValue={chapt.page}
                                        onChange={(event) => this.handleInput(event)}

                                    />
                                </td>

                                <td>
                                    <input
                                        type="text"
                                        placeholder="Chapter Title"
                                        defaultValue={chapt.heading}
                                        onChange={(event) => this.handleInput(event)}
                                    />

                                </td>
                                <td>
                                    <input
                                        type="text"
                                        placeholder="Chapter Description"
                                        defaultValue={chapt.description}
                                        onChange={(event) => this.handleInput(event)}
                                    />

                                </td>

                            </tr>


                        ))
                )


    }

    renderField = () => {
        return (
            <tr>
                <td>
                    <input
                        type="number"
                        placeholder="Page Number"
                        // defaultValue={chapt.page}
                        onChange={(event) => this.handleInput(event)}

                    />
                </td>

                <td>
                    <input
                        type="text"
                        placeholder="Chapter Title"
                        // defaultValue={chapt.heading}
                        onChange={(event) => this.handleInput(event)}
                    />

                </td>
                <td>
                    <input
                        type="text"
                        placeholder="Chapter Description"
                        // defaultValue={chapt.description}
                        onChange={(event) => this.handleInput(event)}
                    />

                </td>

            </tr>


        )
    }



    render() {
        // console.log('rendered');

        let items = this.props.items;

        // console.log(this.state);
        console.log(this.state);

        // this.refineSubcatList();



        return (

            <div className="main_view">



                <div className="rl_container article edit_page">

                    {/* {
                        items.updateItem ?
                            <div className="edit_confirm">
                                Post updated, <Link to={`/items/${items.item._id}`}>
                                    Click here to see your post
                                </Link>
                            </div>
                        : null
                    } */}


                    <form onSubmit={this.onSubmit}>

                        <h2>PDF Chapter Page Index:</h2>





                        <table>
                        <tbody>

                            <tr>
                                <td>Page Number</td>
                                <td>Chapter Title</td>
                                <td>Chapter Description</td>
                            </tr>

                            {this.props.items && this.props.items.item && this.props.items.item.pdf_page_index ?
                                this.renderChapters()
                            : null}

                            {this.renderField()}
                        </tbody>
                        </table>
                    </form>

                    <button type="button" className="half_width_l" onClick={this.addField}>+</button>
                    <button type="submit" className="half_width_l" onClick={this.onSubmit}>Save and Continue</button>








                    {this.state.saved ?
                        <p className="message center">Information saved!</p>
                    : null}


                </div>
            </div>

        );
    }
}

function mapStateToProps(state) {
    return {
        items:state.items
    }
}

export default connect(mapStateToProps)(ChapterIndex)
