export default function(state={}, action) {
    switch(action.type) {

        case 'GET_ITEMS':
            return { 
                ...state,
                list:action.payload 
            };

        case 'GET_ITEM':
            return {
                ...state,
                item:action.payload
            };

        case 'GET_ALL_ITEMS':
            return {
                ...state,
                item:action.payload
            };


        case 'GET_ITEM_W_CONTRIBUTOR':
            return {
                ...state,
                item:action.payload.item,
                contributor:action.payload.contributor
            };

        case 'CLEAR_ITEM_W_CONTRIBUTOR': 
            return {
                ...state,
                item:action.payload.item,
                contributor:action.payload.contributor
            };

        case 'ADD_ITEM':
            return {
                ...state,
                newitem:action.payload
            };

        case 'CLEAR_NEW_ITEM':
            return {
                ...state,
                newitem:action.payload
            };

        case 'UPDATE_ITEM':
            return {
                ...state,
                updateItem:action.payload.success,  // for update message
                item:action.payload.doc
            }

        case 'DELETE_ITEM':
            return {
                ...state,
                postDeleted:action.payload
            }

        case 'CLEAR_ITEM':
            return {
                ...state,
                updateItem:action.payload.updateItem,
                item:action.payload.item,
                postDeleted:action.payload.postDeleted
            }

        default:
            return state;
    }
}
