import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';




import { getItemById, updateItem, clearItem, deleteItem } from '../../actions';
import { getAllColls, getAllCats, getAllSubCats  } from '../../actions';
// import Tags from '../../widgetsUI/tags';



class EditItem extends PureComponent {


    state = {

       dataToUpdate: {
           _id: null,
           category_ref: [],
           subcategory_ref: [],
           tags: [],
           collection_id: null

       },
       catsConverted: [],
       subcatsConverted: [],
       collConverted: [],
       tagsConverted: []

    }


    componentDidMount() {
        this.props.dispatch(getItemById(this.props.match.params.id))
        this.props.dispatch(getAllColls())
        this.props.dispatch(getAllCats());
        this.props.dispatch(getAllSubCats());
    }


    componentWillUnmount() {
        this.props.dispatch(clearItem())
    }


    componentDidUpdate(prevProps) {

        let item = this.props.items.item;
        if (this.props !== prevProps) {
            if (this.props.items && this.props.cats && this.props.colls && this.props.subcats) {


                // REFORMAT EXISTING CATEGORIES
                let catsForState = [];
                this.props.items.item.category_ref.map( (catref) => {
                    this.props.cats.map( (cat) => {
                        if ( cat.cat_id == catref) {
                            catsForState.push(
                                {
                                    id: cat.cat_id,
                                    title: cat.title
                                }
                            )
                        }
                    })
                })


                // REFORMAT EXISTING CATEGORIES
                let subcatsForState = [];
                this.props.items.item.subcategory_ref.map( (subcatref) => {
                    this.props.subcats.map( (subcat) => {
                        if ( subcat.subcat_id == subcatref) {
                            subcatsForState.push(
                                {
                                    id: subcat.subcat_id,
                                    title: subcat.title
                                }
                            )
                        }
                    })
                })





                // REFORMAT EXISTING COLLECTION
                let collForState = null;
                this.props.colls.map( (coll) => {
                    if ( coll.id == this.props.items.item.collection_id) {
                        collForState = {
                            id: coll.id,
                            title: coll.title
                        }
                    }
                })


                let existsForState = {
                    _id: this.props.items.item._id,
                    category_ref: this.props.items.item.category_ref,
                    subcategory_ref: this.props.items.item.subcategory_ref,
                    tags: this.props.items.item.tags,
                    collection_id: this.props.items.item.collection_id
                }

                this.setState({
                    catsConverted: catsForState,
                    subcatsConverted: subcatsForState,
                    collConverted: collForState,
                    tagsConverted: this.props.items.item.tags,
                    dataToUpdate: existsForState
                })
            }
        } 
    }


    deletePost = () => {
        this.props.dispatch(deleteItem(this.state.formdata._id));
        this.props.history.push('/user/all-items');
    }



    redirectUser = (url) => {
        setTimeout(() => {
            this.props.history.push(url)
        }, 1000)
    }




    onSubmit = (e) => {
        e.preventDefault();

    }


    render() {
        if (this.state.tagsConverted.length) {
            console.log(this.state.tagsConverted.length);
        }

        let items = this.props.items;



        return (


            <div className="rl_container article edit_page">
                {/* { this.state.loaded ? */}

                    {
                        items.updateItem ?
                            <div className="edit_confirm">
                                Post updated, <Link to={`/items/${items.item._id}`}>
                                    Click here to see your post
                                </Link>
                            </div>
                        : null
                    }


                    {
                        items.itemDeleted ?
                            <div className="red_tag">
                                Item Deleted    
                                {this.redirectUser('/user/all-items')}
                            </div>

                        : null
                    }


                    <form onSubmit={this.onSubmit}>

                        <h2>Edit item:</h2>

                        <table>
                        <tbody>


                            <tr>
                                <td>
                                    Tags
                                </td>
                                <td>
                                    { this.state.tagsConverted.length  ?
                                        this.state.tagsConverted[0].label
                                    : null}
                                     <br/>
                                    {this.state.tagsConverted.length ?
                                        <CreatableSelect
                                            defaultValue={this.tagsConverted}
                                            isMulti
                                            onChange={this.handleChangeTags}
                                            options={this.state.tagsConverted}
                                        />
                                    : null}
                                </td>
                            </tr>





                            <tr>
                                <td>
                                    Collection
                                </td>
                                <td>
                                    { this.state.collConverted ?
                                        this.state.collConverted.title
                                    : null}
                                    <br/>
                                    <div className="form_element">
                                        {this.state.collConverted ?
                                            <Select
                                                className="basic-single"
                                                classNamePrefix="select"
                                                defaultValue={this.state.collConverted}
                                                isDisabled={false}
                                                isLoading={false}
                                                isClearable={true}
                                                isRtl={false}
                                                isSearchable={true}
                                                name="color"
                                                options={this.state.collConverted}
                                                onChange={this.handleChangeColl}
                                            />
                                        : null}

                                    </div>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    Category
                                </td>
                                <td>
                                    { this.state.catsConverted.length  ?
                                        this.state.catsConverted[0].title
                                    : null}
                                    <br/>
                                    { this.props.items && this.props.items.item && this.props.items.item.category_ref && this.props.items.item.category_ref[0] ?
                                        this.props.items.item.category_ref.map( (cat, i) => (
                                        <span key={i}>{cat} </span>
                                        ))
                                    : null}
                                    {/* <div className="form_element">


                                        {this.state.updated && this.state.catsAreUpdated && this.props.items && this.props.items.item ?
                                            <Select
                                                key={`cat_${this.props.items.item._id}`}
                                                defaultValue={this.state.catsConverted}
                                                isMulti
                                                name="colors"
                                                options={this.state.catOptions}
                                                className="basic-multi-select"
                                                classNamePrefix="select"
                                                onChange={this.handleChangeCats}
                                            />
                                        : null}



                                    </div> */}
                                </td>
                            </tr>  



                            <tr>
                                <td>
                                    Sub-categories 
                                </td>
                                <td>
                                    { this.state.subcatsConverted.length  ?
                                        this.state.subcatsConverted[0].title
                                    : null}
                                     <br/>
                                    { this.props.items && this.props.items.item && this.props.items.item.subcategory_ref && this.props.items.item.subcategory_ref[0] ?
                                        this.props.items.item.subcategory_ref.map( (cat, i) => (
                                        <span key={i}>{cat} </span>
                                        ))
                                    : null}
                                    {/* <div className="form_element">
                                        Selection here

                                    </div> */}
                                </td>
                            </tr>

                            <tr>

                            </tr>

                        </tbody>
                        </table>

                        <button type="submit">Submit Edit</button>

                        <div className="delete_post">
                            <div className="button" onClick={(e) => { if (window.confirm('Are you sure you wish to delete this item?')) this.deletePost(e) } }>
                                Delete item
                            </div>
                        </div>

                    </form>
            </div>

        );
    }
}

function mapStateToProps(state) {
    return {
        items:state.items,
        colls:state.collections.colls,
        cats:state.cats.cats,
        subcats:state.cats.subcats
    }
}

export default connect(mapStateToProps)(EditItem)
