import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { getInfoText } from '../../actions';


class Info extends Component {

    componentDidMount() {
        this.props.dispatch(getInfoText());
    }



    renderInfo = () => {
        return (
            <div>
                <div>
                    {this.props.text.heading_1 ?
                        <h3>{this.props.text.heading_1}</h3>
                    : null}

                    {this.props.text.paragraph_1 ?
                        <p>{this.props.text.paragraph_1}</p>
                    : null}
                </div>


                <div>
                    {this.props.text.heading_2 ?
                        <h3>{this.props.text.heading_2}</h3>
                    : null}

                    {this.props.text.paragraph_2 ?
                        <p>{this.props.text.paragraph_2}</p>
                    : null}
                </div>


                <div>
                    {this.props.text.heading_3 ?
                        <h3>{this.props.text.heading_3}</h3>
                    : null}

                    {this.props.text.paragraph_3 ?
                        <p>{this.props.text.paragraph_3}</p>
                    : null}
                </div>


                <div>
                    {this.props.text.heading_4 ?
                        <h3>{this.props.text.heading_4}</h3>
                    : null}

                    {this.props.text.paragraph_4 ?
                        <p>{this.props.text.paragraph_4}</p>
                    : null}
                </div>





            </div>
        )
    }


    render() {
        console.log(this.props)
        return (
            <div>
                {this.props.text ?
                    this.renderInfo()

                :null }
            </div>
        );
    }
}


function mapStateToProps(state) {
    // console.log(state);

    return {
        text: state.infos.text

    }
}


export default connect(mapStateToProps)(Info)
