import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { addItem, clearNewItem } from '../../actions';

class AddItem extends Component {


    state = {
        formdata:{
            title: '',
            creator: '',
            subject: '',
            description: '',
            source: '',
            date_created: '',
            tags: '',
            contributor: '',
            collection_id: '',
            category: '',
            sub_category: '',        
            item_format: '',
            materials: '',
            physical_dimensions: '',
            pages: '',        
            editor: '',
            publisher: '',
            further_info: '',
            external_link: '',
            language: '',
            reference: '',
            rights: '',
            file_format: '',
            address: ''

            // name:'',
            // author:'', 
            // review:'',
            // pages:'',
            // rating:'',
            // price:''
        }
    }


    handleInput = (event, name) => {
        // make a copy of formdata
        const newFormdata = {
            ...this.state.formdata
        }

        // populate the copy with the input value
        newFormdata[name] = event.target.value;

        // copy it back to state
        this.setState({
            formdata:newFormdata
        })
    }


    // showNewBook = (book) => (
    //     book.post ?
    //         <div className="conf_link">
    //             Cool !! <Link to={`/books/${book.bookId}`}>
    //                 Click the link to see the post
    //             </Link>
    //         </div>
    //     : null
    // )


    showNewItem = (item) => (
        item.post ?
            <div className="conf_link">
                Cool !! <Link to={`/items/${item.itemId}`}>
                    Click the link to see the item
                </Link>
            </div>
        : null
    )



    createTextInput = (stateVar, name, placeholder, type) => {
        // let string = `this.state.formdata.${variable}`;

        return (
            <div className="form_element">
                <input
                    type="text"
                    placeholder={placeholder}
                    value={stateVar} 
                    onChange={(event) => this.handleInput(event, name)}
                />
            </div>
        )
    }



    createSelectInput = (db, value, name, placeholder) => {
        return (
            <div className="form_element">
                <select
                    value={value}
                    onChange={(event) => this.handleInput(event, name)}
                >
                    <option value="" disabled selected>{placeholder}</option>

                    {db.map( (option, i) => (
                        <option key={i} value={option.id}>{option.title}</option>
                    ) )}
                </select>
            </div>
        )
    }

    // createSelectInput(cateogries, this.state.formdata.subcateogry, 'subcategory', "Sub-cateogry")


    submitForm = (e) => {
        e.preventDefault();

        // console.log(this.state.formdata);

        // dispatch an action, adding updated  formdata + the user id from the redux store
        this.props.dispatch(addItem({
            ...this.state.formdata,
            ownerId:this.props.user.login.id
        }))
    }


    // clear success message 
    componentWillUnmount() {
        this.props.dispatch(clearNewItem())
    }


    render() {
        // console.log(this.props);
        return (
            <div className="rl_container article">
                <form onSubmit={this.submitForm}>

                    {/* <h2>Add a review</h2> */}
                    <h2>Add an item</h2>




                    {this.createTextInput(this.state.formdata.name,'name', "Enter item title")}
                    {this.createTextInput(this.state.formdata.creator,'creator', "Item creator")}
                    {this.createTextInput(this.state.formdata.subject,'subject', "Item subject")}

                    <textarea
                        placeholder="Describe the item here..."
                        value={this.state.formdata.description}
                        onChange={(event) => this.handleInput(event, 'description')}
                    />


                    {this.createTextInput(this.state.formdata.source,'source', "Item source")}
                    {this.createTextInput(this.state.formdata.date_created,'date_created', "Item date_created")}
                    {this.createTextInput(this.state.formdata.tags,'tags', "Item tags")}
                    {this.createTextInput(this.state.formdata.contributor,'contributor', "Item contributor")}


                    <div className="form_element">
                        <select
                            value={this.state.formdata.collection}
                            onChange={(event) => this.handleInput(event, 'rating')}
                        >
                            <option value="" disabled selected>Collection</option>
                            <option val="1">Hugh Lane Collection</option>
                            <option val="2">James Collins Collection</option>
                            <option val="3">National Photographic Collection</option>
                            <option val="4">Micheal O hAodha</option>
                            <option val="5">National Library of Ireland</option>

                        </select>
                    </div>

                    <div className="form_element">
                        <select
                            value={this.state.formdata.category}
                            onChange={(event) => this.handleInput(event, 'cateogry')}
                        >   
                            <option value="" disabled selected>Category</option>
                            <option val="1">Hugh Lane Collection</option>
                            <option val="2">James Collins Collection</option>
                            <option val="3">National Photographic Collection</option>
                            <option val="4">Micheal O hAodha</option>
                            <option val="5">National Library of Ireland</option>

                        </select>
                    </div>                    

                    <div className="form_element">
                        <select
                            value={this.state.formdata.subcateogry}
                            onChange={(event) => this.handleInput(event, 'subcateogry')}
                        >
                            <option value="" disabled selected>Sub Category</option>
                            <option val="1">Hugh Lane Collection</option>
                            <option val="2">James Collins Collection</option>
                            <option val="3">National Photographic Collection</option>
                            <option val="4">Micheal O hAodha</option>
                            <option val="5">National Library of Ireland</option>

                        </select>
                    </div>


                    {this.createTextInput(this.state.formdata.item_format,'item_format', "Item item_format")}
                    {this.createTextInput(this.state.formdata.materials,'materials', "Item materials")}
                    {this.createTextInput(this.state.formdata.physical_dimensions,'physical_dimensions', "Item physical_dimensions")}


                    <div className="form_element">
                        <input
                            type="number"
                            placeholder="Number of pages"
                            value={this.state.formdata.pages} 
                            onChange={(event) => this.handleInput(event, 'pages')}                        />
                    </div>

                    {this.createTextInput(this.state.formdata.editor,'editor', "Item editor")}
                    {this.createTextInput(this.state.formdata.publisher,'publisher', "Item publisher")}
                    {this.createTextInput(this.state.formdata.further_info,'further_info', "Item further_info")}
                    {this.createTextInput(this.state.formdata.external_link,'external_link', "Item external_link")}
                    {this.createTextInput(this.state.formdata.language,'language', "Item language")}
                    {this.createTextInput(this.state.formdata.reference,'reference', "Item reference")}
                    {this.createTextInput(this.state.formdata.rights,'rights', "Item rights")}
                    {this.createTextInput(this.state.formdata.file_format,'file_format', "Item file_format")}
                    {this.createTextInput(this.state.formdata.address,'address', "Item address")}







                    <button type="submit">Submit Item</button>

                    {
                        // if new book exists
                        this.props.items.newitem ?
                            this.showNewItem(this.props.items.newitem)
                        : null


                    }


                </form>
            </div>
        );
    }
}

function mapStateToProps(state) {
    // console.log(state); 
    return {
        items:state.items
    }
}

export default connect(mapStateToProps)(AddItem)
