import React, { Component } from 'react';
import { connect } from 'react-redux';

import BookItem from '../widgetsUI/book_item';
// import actions
// import { getBooks } from '../actions';  // OLD!
import { getItems } from '../actions';
import NewsSlider from '../widgetsUI/Slider/slider';




class HomeContainer extends Component {


    // componentWillMount() {
    componentDidMount() {

        // this.props.dispatch(getBooks(1,0,'desc')); // OLD!
        this.props.dispatch(getItems(4,0,'asc'));

    }



    // renderItems = (books) => (   // OLD!
    //     books.list ?
    //         // console.log(books.list.length)
    //         books.list.map( (item, i) => (
    //             <BookItem {...item} key={item._id}/>
    //         ))
    //     : null
    // )


    renderItems = (items) => (
        items.list ?    // list is an array of previously called items!
            // console.log(books.list.length)
            items.list.map( (item, i) => (
                <BookItem {...item} key={item._id}/>
            ))
        : null
    )


    // loadmore = () => {
    //     let count = this.props.books.list.length;
    //     // console.log(count);
    //     this.props.dispatch(getBooks(1,count,'desc',this.props.books.list))
    // }

    loadmore = () => {
        let count = this.props.items.list.length;
        // console.log(count);
        this.props.dispatch(getItems(4,count,'asc',this.props.items.list))
    }



    render() {
        // console.log(this.props.books.list);
        return (
            <div> 
                This is the HomeContainer component (home_container.js)
                <NewsSlider
                    type="featured"
                    start={0}
                    amount={3}
                    settings={{
                    dots: false
                    }}

                />
                {this.renderItems(this.props.items)}

                <div 
                    className="loadmore"
                    onClick={this.loadmore}
                >Load More</div>
            </div>
        );
    }
}


function mapStateToProps(state) {
    return {
        // books:state.books
        items:state.items
    }
}


export default connect(mapStateToProps)(HomeContainer);
