import SearchHeader from './search_header';
import NewsList from './news_list';
import { getAllItems } from '../../actions';





class Search extends Component {

    state = {
        filtered: []
    }

    componentDidMount() {
        this.props.dispatch(getAllItems());

    }

    getKeywordTitle = (event) => {
