    }


    static getDerivedStateFromProps(nextProps, prevState) {

        // let newCatOptions = prevState.catOptions;
        // let newSelected = prevState.selected;
        let tempCollOptions = [];
        let tempCollSelection = [];

        console.log(nextProps);

        if (nextProps.colls && nextProps.colls.length) {
            // create list of all collection titles and ids
            nextProps.colls.map( coll => {
                tempCollOptions.push({
                    value: coll.id,
                    label: coll.title
                })
            })

            // chack for any categories already set
            if (nextProps.items && nextProps.items.item && nextProps.items.item.collection_id) {
                nextProps.colls.map ( coll => {
                    if (coll.id == nextProps.items.item.collection_id) {
                        collTitle = coll.title;

                        tempCollSelection.push({
                            value: nextProps.items.item.collection_id,
                            label: collTitle
                        })
                    }
                })
            }

            return {
                collOptions: tempCollOptions,
                collSelection: tempCollSelection,
                updated: true
            }




        }


        // // check if props have arrived
        // if (nextProps.cats && nextProps.cats.length) {
        //     // create list of all categories and ids
        //     nextProps.cats.map( (cat, i) => {
        //         newCatOptions.push({
        //             value: cat.cat_id,
        //             label: cat.title
        //         })
        //     })

        //     // chack for any categories already set
        //     if (nextProps.items && nextProps.items.item && nextProps.items.item.category_ref && nextProps.items.item.category_ref.length ) {

        //         nextProps.items.item.category_ref.map( (catref, i) => {

        //             let catTitle;

        //             nextProps.cats.map( (cat, i) => {
        //                 if (cat.cat_id == catref) {
        //                     catTitle = cat.title;

        //                     newSelected.push({
        //                         value: catref,
        //                         label: catTitle
        //                     })
        //                 }
        //             })
        //         })

        //     }
        // }
        // return {
        //     catOptions: newCatOptions,
        //     selected: newSelected,
        //     updated: true
        // }
    }


    handleChange = (newValue) => {
        if (newValue && newValue.length) {
            this.setState({ 
                savedCats: [...newValue]
            });
        // newValue is not an array if empty
        } else {
            this.setState({ 
                savedCats: []
            });
        }


    };

    onClickHandler = () => {

        let updateData = {
            _id: '5edcbc5027c3271205b2360a',
            category_ref: []
        };

        if (this.state.savedCats && this.state.savedCats.length) {
            this.state.savedCats.map( cat => {
                updateData.category_ref.push(cat.value);
            })
        } 

        this.props.dispatch(updateItem({
            ...updateData
        }))
    }

    render() {


        const {
            isClearable,
            isSearchable,
            isDisabled,
            isLoading,
            isRtl,
          } = this.state;

        const testOptions = this.state.testOptions;

        return (
            <div>
                {this.props.items.item && this.props.items.item.title ?
                    <h2>{this.props.items.item.title}</h2>
                : null }

                <p>Collections:</p>

                {this.state.updated ?
                    <Select
                        className="basic-single"
                        classNamePrefix="select"
                        defaultValue={testOptions[0]}
                        isDisabled={isDisabled}
                        isLoading={isLoading}
                        isClearable={isClearable}
                        isRtl={isRtl}
                        isSearchable={isSearchable}
                        name="color"
                        options={testOptions}
                    />
                : null}

                <button type="button"onClick={this.onClickHandler}>Submit</button> 

            </div>
        );
    }
};


function mapStateToProps(state) {
    // console.log(state);
    return {
        items:state.items,
        colls:state.collections.colls
    }
}

export default connect(mapStateToProps)(Sandbox);
