import axios from 'axios';



export function getBooks(
    limit = 10,
    start = 0,
    order = 'asc',
    list = ''
) {
    const request = axios.get(`/api/books?limit=${limit}&skip=${start}&order=${order}`)
                        .then(response => response.data)
    console.log(request);

    return{
        type:'GET_BOOKS',
