import React, { Component } from 'react';
import { connect } from 'react-redux';


import { getColl } from '../../actions';


class Collection  extends Component {



    componentDidMount() {
        this.props.dispatch(getColl(this.props.match.params.id))
    }


    renderColl = (coll) => {
      let imageId = null;

      if (coll) {
          imageId = coll[0].cover_item;
      }

      return ( coll ?
        <div>
          <h2>Title</h2> {coll[0].title}
          <h2>Image</h2> <img src={`../../../public/images/sq_thumb/1.jpg`} />
          {/* url(/images/sq_thumb/${item.omeka.omeka_id}.jpg) */}
          <h2>Description</h2> {coll[0].description}
          <h2>Subject</h2> {coll[0].subject}
          <h2>Format</h2>{coll[0].format}

          <h2>Items</h2>

        </div>
        : null
      )

    }



    render() {
        console.log(this.props.coll);

        let coll = this.props.coll;


        return (

              <div>

                  {this.renderColl(coll)}



              </div>
        )
    }
}


function mapStateToProps(state) {
    return {
        coll: state.collections.coll
    }
}


export default connect(mapStateToProps)(Collection)
