import React from 'react';
import Select from 'react-select';

import { getAllCats } from '../../actions';




class Sandbox extends React.Component {

    catOptions = [
        { value: '1', label: 'Geography' },
        { value: '2', label: 'History' },
        { value: '3', label: 'Literature' },
        { value: '4', label: 'Photography' },
        { value: '5', label: 'Music' },

        { value: '6', label: 'Theatre' },
        { value: '7', label: 'Visual Arts' },
        { value: '8', label: 'Language' },
        { value: '9', label: 'Heritage' },
        { value: '10', label: 'Politics' },
        { value: '11', label: 'Film' },
        { value: '12', label: 'Radio' },
        { value: '13', label: 'Traveller Organisations' }
    ]

    componentDidMount() {
        this.props.dispatch(getAllCats());
    }



    render() {

        console.log(this.props)

        const catOptions = this.catOptions;

        return (
            <div>
                <h2>Categories:</h2>

                <Select
                    defaultValue={[catOptions[2], catOptions[3]]}
                    isMulti
                    name="colors"
                    options={catOptions}
                    className="basic-multi-select"
                    classNamePrefix="select"
                />
            </div>
        );
    }


};


function mapStateToProps(state) {
    // console.log(state); 
    return {
        cats:state.cats.cats
    }
}



export default connect(mapStateToProps)(Sandbox);
