import React, { Component } from 'react';
import { connect } from 'react-redux';
import moment from 'moment-js';
import { Link } from 'react-router-dom';

// import { getUserPosts } from '../../actions';
import { getUserItems } from '../../actions';



class UserItems extends Component {


    // componentWillMount() {
    componentDidMount() {
        this.props.dispatch(getUserItems(this.props.user.login.id))
    }



    showUserItems = (user) => (
        user.userPosts ?
            user.userPosts.map(item => (
                <tr key={item._id}> 
                    <td>
                        <Link to={`/user/edit-post/${item._id}`}>
                            {item.title}
                        </Link>
                    </td>
                    <td>{item.creator}</td>
                    <td>{moment(item.createAt).format("MM/DD/YY")}</td>
                </tr>
            ))
        : null
    )


    render() {
        // console.log(this.props);

        let user = this.props.user;

        return (
            <div className="user_posts">
                {/* <h4>Your reviews:</h4> */}
                <h4>Your items:</h4>

                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Author</th>
                            <th>Date</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.showUserItems(user)}
                    </tbody>
                </table>

            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        user:state.user
    }
}


export default connect(mapStateToProps)(UserItems)
