import React from 'react';
import { connect } from 'react-redux';
// import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';


import { getAllCats, getItemById, updateItem } from '../../actions';


class Sandbox extends React.Component {

    state = {
        existingTags: [],
        updatedTags: [],
        updated: false
    }

    componentDidMount() {
        this.props.dispatch(getItemById('5edcbc5027c3271205b2360a'));
    }


    static getDerivedStateFromProps(nextProps, prevState) {
        let tagsFromProps = [];

        if (nextProps.items.item && nextProps.items.item.tags) {
            nextProps.items.item.tags.map( tag => {
                tagsFromProps.push({
                    value: tag.value,
                    label: tag.label
                })
            })

        }
        return {
            existingTags: tagsFromProps,
            updated: true
        }
    }


    handleChange = (newValue) => {
        if (newValue && newValue.length) {
            console.log(newValue)
            this.setState({ 
                updatedTags: [...newValue]
            });

        } else {
            this.setState({ 
                updatedTags: []
            });
        }
    };

    onClickHandler = () => {
        let updateData = {
            _id: '5edcbc5027c3271205b2360a',
            tags: []
        };

        if (this.state.updatedTags && this.state.updatedTags.length) {
            this.state.updatedTags.map( tag => {
                updateData.tags.push(tag);
            })
        } 
        // console.log(updateData);
        this.props.dispatch(updateItem({
            ...updateData
        }))
    }

    render() {

        console.log(this.state.updatedTags);

        const updatedTags = this.state.updatedTags;

        return (
            <div>
                {this.props.items.item && this.props.items.item.title ?
                    <h2>{this.props.items.item.title}</h2>
                : null }

                {this.state.updated && this.state.existingTags.length && this.props.items?
                    <CreatableSelect
                        defaultValue={this.state.existingTags}
                        isMulti
                        onChange={this.handleChange}
                        options={updatedTags}
                    />
                : null}

                <button type="button"onClick={this.onClickHandler}>Submit</button> 
            </div>
        );
    }
};


function mapStateToProps(state) {
    return {
        items:state.items,
        cats:state.cats.cats
    }
}

export default connect(mapStateToProps)(Sandbox);
