import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {Progress} from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


import { addItem, clearNewItem} from '../../actions';
import {getAllColls, getAllCats, getAllSubCats  } from '../../actions';
import Tags from '../../widgetsUI/tags';





class AddItem extends Component {


    state = {
        formdata:{
            title: '',
            creator: '',
            subject: '',
            description: '',
            source: '',
            date_created: '',
            contributor: '',
            collection_id: '',     
            item_format: '',
            materials: '',
            physical_dimensions: '',
            pages: '',        
            editor: '',
            publisher: '',
            further_info: '',
            language: '',
            reference: '',
            rights: '',
            file_format: '',
            address: '',
            category_ref: '',
            subcategory_ref: '',
            tags: {
                id: '',
                text: ''
            },
            external_link: {
                url: '',
                text: ''
            },
            geo: {
                address: ''
            }

        },        
        selectedFile: null,
        loaded: 0
    }


    componentDidMount() {
        this.props.dispatch(getAllColls())
        this.props.dispatch(getAllCats());
        this.props.dispatch(getAllSubCats());
    }


    // clear success message 
    componentWillUnmount() {
        this.props.dispatch(clearNewItem())
    }

    handleInput = (event, name, level) => {
        // make a copy of formdata
