import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

// import PanelView from '../PanelView/panel_view';




import { getCollById, searchItem } from '../../actions';
// import { getCollWithItems } from '../../actions';
import NavigationBar from '../../widgetsUI/navigation';



class Collection  extends Component {



    navInfo = {
        catTitle: null,
        catId: null,
        subCatTitle: null,
        subCatId: null,
        collTitle: null,
        collId: null,
        type: 'Collections'
    }


    componentDidMount() {
        let queryId = this.props.match.params.id;



        this.props.dispatch(getCollById(queryId));
        this.props.dispatch(searchItem('collection_id', queryId))

    }



    state = {
        info: []
    }

    addDefaultImg = (ev) => {
        const newImg = '/images/default/default.jpg';
        if (ev.target.src !== newImg) {
            ev.target.src = newImg
        }  
    }


    renderColl = (coll) => {
        let imageId = null;

        if (coll && coll.length) {
          imageId = coll[0].cover_item;

        }
        return ( coll && coll.length ?
            <div>
                <h1>{coll[0].title}</h1> 
                <hr />

                <h2>Description</h2> {coll[0].description}
                <h2>Subject</h2> {coll[0].subject}
                { coll[0].format ? 
                    <span><h2>Format</h2>{coll[0].format}</span>
                : null }
                <hr />
                <h1>Items</h1>
                <hr />
            </div>
            : null
        )

    }





    renderGrid = () => {
        return(
            <div class="coll_grid_row">
                <div className="coll_grid_column">






                    { this.props.items ? 
                        this.props.items.map ( (item, i) => (
                            <Link to={`/items/${item._id}`}key={i}>
                            <figure key={i}>
                                    <img src={`/images/items/${item._id}/sq_thumbnail/0.jpg`} alt={item.title} onError={this.addDefaultImg} />
                                    <figcaption>{item.title}</figcaption>
                                </figure>
                                </Link>
                        ))
                    : null }





                </div>
            </div>
    )}




    renderItems = (items) => (
        items ?
            items.map( (item, i) => (
              <Link to={`/items/${item._id}`}key={i}>
                <h2>{item.title}</h2> 
                <img src={`/images/items/${item._id}/sq_thumbnail/0.jpg`} alt={"Item"} onError={this.addDefaultImg}/>
                <h3>Creator</h3> {item.creator}
                <h3>Subject</h3> {item.subject}
                <h3>Description</h3> {item.description}
                <hr />
              </Link>

            ))
        : null

    )






    componentWillReceiveProps(nextProps) {
        if(nextProps.items) {
            let tempArray = [];
            nextProps.items.map( item => {
                tempArray.push(
                    {
                        src: `/images/items/${item._id}/sq_thumbnail/0.jpg`,
                        caption: item.title,
                        link: `/items/${item._id}`
                    }
                )
            })
            if (tempArray.length) {
                this.setState({ info: [...tempArray]});
            }
        }
    }










    getCollName = () => {
        if (this.props.coll && this.props.coll.length) {
            this.navInfo.collTitle = this.props.coll[0].title;
            this.navInfo.collId = this.props.coll[0].id;
        }
    }




    render() {

        // console.log(this.props.coll);
        console.log(this.props);
        // this.setNav(this.state.coll);
        this.getCollName();


        return (

            <div className="main_view">
                  {/* { this.state.navInfo ? */}
                    <NavigationBar navinfo={this.navInfo}/>
                  {/* : null} */}

                  {this.renderColl(this.props.coll)}


                  {this.renderGrid()}

              </div>
        )
    }
}


function mapStateToProps(state) {
    console.log(state);
    return {
        items: state.items.data,
        coll: state.collections.coll

    }
}


export default connect(mapStateToProps)(Collection)
