import React, { Component } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';


import { getInfoText, updateInfoText } from '../../actions';



class AdminInfo extends Component {


    state= {
        formdata: {
            heading_1: '',
            heading_2: '',
            heading_3: '',
            heading_4: '',
            paragraph_1: '',
            paragraph_2: '',
            paragraph_3: '',
            paragraph_4: ''
        }
    }

    componentDidMount() {
        this.props.dispatch(getInfoText());

    }


    componentDidUpdate(prevProps, prevState) {
        if (this.props != prevProps) {

            const infotext = this.props.infotext;

            let formdata = {
                heading_1: infotext.heading_1,
                heading_2: infotext.heading_2,
                heading_3: infotext.heading_3,
                heading_4: infotext.heading_4,
                paragraph_1: infotext.paragraph_1,
                paragraph_2: infotext.paragraph_2,
                paragraph_3: infotext.paragraph_3,
                paragraph_4: infotext.paragraph_4
            }

            this.setState({
                formdata
            })
        }
    }


    render() {

        console.log(this.props)



        return (



            <div className="admin">
                <div className="admin_intro">

                    <h1>Change the info about the info page</h1>


                    <form onSubmit={this.submitForm}>
                        <table>
                            <tbody>






                            {/* PARAGRAPH ONE */}

                            <tr>
                                <td>
                                    <span>Paragraph 1 Heading</span>
                                </td>

                                <td>
                                    <input
                                        type="text"
                                        placeholder="Paragraph 1 Heading"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                    />
                                </td>
                            </tr>


                            <tr>
                                <td>
                                    <span>Paragraph 1 Body</span>
                                </td>
                                <td>
                                    <textarea
                                        type="text"
                                        placeholder="Paragraph 1 Content"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                        rows={6}
                                    />
                                </td>
                            </tr>

                            <tr>
                                <p>[Paragraph 1 image]</p>
                            </tr>


                            {/* PARAGRAPH TWO */}

                            <tr>
                                <td>
                                    <span>Paragraph 2 Heading</span>
                                </td>

                                <td>
                                    <input
                                        type="text"
                                        placeholder="Paragraph 2 Heading"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                    />
                                </td>
                            </tr>


                            <tr>
                                <td>
                                    <span>Paragraph 2 Body</span>
                                </td>
                                <td>
                                    <textarea
                                        type="text"
                                        placeholder="Paragraph 2 Content"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                        rows={6}
                                    />
                                </td>
                            </tr>

                            <tr>
                                [Paragraph 2 image]
                            </tr>

                            {/* PARAGRAPH THREE */}

                            <tr>
                                <td>
                                    <span>Paragraph 3 Heading</span>
                                </td>

                                <td>
                                    <input
                                        type="text"
                                        placeholder="Paragraph 3 Heading"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                    />
                                </td>
                            </tr>


                            <tr>
                                <td>
                                    <span>Paragraph 3 Body</span>
                                </td>
                                <td>
                                    <textarea
                                        type="text"
                                        placeholder="Paragraph 3 Content"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                        rows={6}
                                    />
                                </td>
                            </tr>

                            <tr>
                                [Paragraph 3 image]
                            </tr>

                            {/* PARAGRAPH FOUR */}

                            <tr>
                                <td>
                                    <span>Paragraph 4 Heading</span>
                                </td>

                                <td>
                                    <input
                                        type="text"
                                        placeholder="Paragraph 4 Heading"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                    />
                                </td>
                            </tr>


                            <tr>
                                <td>
                                    <span>Paragraph 4 Body</span>
                                </td>
                                <td>
                                    <textarea
                                        type="text"
                                        placeholder="Paragraph 4 Content"
                                        defaultValue={null} 
                                        onChange={(event) => this.handleInput(event)}
                                        rows={6}
                                    />
                                </td>
                            </tr>

                            <tr>
                                [Paragraph 4 image]
                            </tr>




                        </tbody>
                        </table>
                    </form>


                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    // console.log(state);

    return {
        infotext: state.infos.text

    }
}


export default connect(mapStateToProps)(AdminInfo)
