import React, { Component } from 'react';
import { connect } from 'react-redux';
import moment from 'moment-js';
import { Link } from 'react-router-dom';

// import { getUserPosts } from '../../actions';
import { getUserItems, getAllItems } from '../../actions';



class Sandbox extends Component {


    // componentWillMount() {
    componentDidMount() {
        this.props.dispatch(getUserItems(this.props.user.login.id))
        this.props.dispatch(getAllItems())

    }



    showUserItems = (items) => (
        items.items ?
            items.items.map(item => (
                <span key={item._id}> 


							{/* <span>cp ./sq_thumb/</span>
                            { item.omeka ?
								item.omeka.omeka_id
                            : null}
							<span>.jpg</span> */}



							<span>cp ./sq_thumb/</span>
							{ item.omeka ? item.omeka.omeka_id: null}
							<span>-6.jpg ./byid/</span>
							{ item.omeka ? item._id: null}
							<span>/sq_thumbnail/5.jpg; </span>


							{/* <span>;</span> */}
							{/* <br/> */}
                </span>
            ))
        : null
    )


    render() {
        // console.log(this.props);

        let items = this.props.items;

        return (
            <div className="user_posts">

                        {this.showUserItems(items)}

            </div>
        );
    }
}

function mapStateToProps(state) {

    console.log(state);
    return {
        user:state.user,
        items:state.items
    }
}


export default connect(mapStateToProps)(Sandbox)
