import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';


// import { getBookWithReviewer, clearBookWithReviewer } from '../../actions'; // OLD !
import { getItemWithContributor, clearItemWithContributor, getAllCats, getAllSubCats, getNextItem, getPrevItem } from '../../actions';
import NavigationBar from '../../widgetsUI/navigation';



class ItemView extends Component {




    // componentWillMount() {
    componentDidMount() {
        this.props.dispatch(getAllCats());
        this.props.dispatch(getAllSubCats());
        this.props.dispatch(getItemWithContributor(this.props.match.params.id));
        this.props.dispatch(getNextItem(this.props.match.params.id));
        this.props.dispatch(getPrevItem(this.props.match.params.id));



    }

    componentWillReceiveProps() {

    }


    componentWillUnmount() {
        this.props.dispatch(clearItemWithContributor());
    }


    catTitle = null;

    getCatName = (catId) => {
        if (this.props.cats && this.props.cats.length) {
            this.props.cats.map( cat => {

                if (cat.cat_id == catId[0]) {
                    // console.log(cat.title);
                    this.catTitle = cat.title;
                }
            })
        }
    }


    nextItem = () => {

    }

    renderItem = (items) => {
        // if (items.item && items.item.length ) {
        //     console.log(items.item.omeka.omeka_id);
        // }

        return ( items.item ?

            <div>
                { this.catTitle  ?
                    <NavigationBar category={this.catTitle} subcat={items.item.subcategory_ref} title={items.item.title}/>    
                : null }
                <div className="br_container">

                    <div className="br_header">
                        <h2>{items.item.title}</h2>
                        { items.item.omeka.omeka_id ?
                            <img src={`/images/sq_thumb/${items.item.omeka.omeka_id}.jpg`} alt="Item"/>
                            // <img src={`/images/items/${items.item._id}/sq_thumbnail/0.jpg`} alt="Item"/>
                        : null}
                        <h5>Creator: {items.item.creator}</h5>

                        <div className="br_reviewer">
                            {/* <span>Reviewed by: </span>{items.reviewer.name} {items.reviewer.lastname} */}
                            <span>Reviewed by: </span>{items.contributor.name} {items.contributor.lastname}
                        </div>
                    </div>

                    <div className="br_review">
                        <p>Subject: {items.item.subject}</p>
                        <p>Description: {items.item.description}</p>
                        <p>Source: {items.item.source}</p>
                        <p>Date created: {items.item.date_created}</p>
                        <p>Contributor: {items.item.contributor}</p>

                    </div>

                    <div className="br_review">
                    </div>






                    <div className="br_box">
                        <div className="left">
                            <div>
                                <span>Pages:</span> {items.item.pages}
                            </div>
                            <div>
                                <span>ID:</span> {items.item.id}
                            </div>
                        </div>
                        <div className="right">
                            <span>ID</span>
                            <div>{items.item.id}</div>
                        </div>
                    </div>


                </div>
            </div>
        : null
    )}


    render() {

        console.log(this.props);


        let items = this.props.items;
        // console.log(items.item);

        if (items.item && items.item.category_ref ) {
            this.getCatName(items.item.category_ref)
        }

        return (
            <div>
                {this.renderItem(items)}

                {this.props.previtem ?
                    <Link to={`/items/${this.props.previtem._id}`}>
                        <span>PREV: {this.props.previtem.title} | </span>
                    </Link>

                : null }

                {this.props.nextitem ?
                    <Link to={`/items/${this.props.nextitem._id}`}>
                        NEXT: {this.props.nextitem.title}
                    </Link>

                : null }
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        items: state.items,
        cats: state.cats.cats,
        nextitem: state.items.nextitem,
        previtem: state.items.previtem

    }
}


export default connect(mapStateToProps)(ItemView)
