import React from 'react';
import { connect } from 'react-redux';
import Select from 'react-select';

import { getAllColls, getItemById, updateItem } from '../../actions';


class Sandbox extends React.Component {

    state = {
        // catOptions: [],
        // selected: [],
        // savedCats: [],
        testOptions: [
            { value: '1', label: 'Geography' },
            { value: '2', label: 'History' },
            { value: '3', label: 'Literature' },
            { value: '4', label: 'Photography' },
            { value: '5', label: 'Music' },

            { value: '6', label: 'Theatre' },
            { value: '7', label: 'Visual Arts' },
            { value: '8', label: 'Language' },
            { value: '9', label: 'Heritage' },
            { value: '10', label: 'Politics' },
            { value: '11', label: 'Film' },
            { value: '12', label: 'Radio' },
            { value: '13', label: 'Traveller Organisations' }
        ],
        collOptions: [],
        collSelection: null,
        newSelection: null,
        savedColl: null,
        updated: false,

        isClearable: true,
        isDisabled: false,
        isLoading: false,
        isRtl: false,
        isSearchable: true,
      };

    componentDidMount() {
        this.props.dispatch(getAllColls());
        this.props.dispatch(getItemById('5edcbc5027c3271205b2360a'));
    }


    static getDerivedStateFromProps(nextProps, prevState) {

        console.log(prevState);

        // let newCatOptions = prevState.catOptions;
        // let newSelected = prevState.selected;
        let tempCollOptions = [];
        let tempCollSelection = null;

        // console.log(nextProps);

        if (nextProps.colls && nextProps.colls.length) {
            // create list of all collection titles and ids
            nextProps.colls.map( coll => {
                tempCollOptions.push({
                    value: coll.id,
                    label: coll.title
                })
            })

            // chack for any categories already set
            if (nextProps.items && nextProps.items.item && nextProps.items.item.collection_id) {
                nextProps.colls.map ( coll => {
                    let collTitle = null;
                    if (coll.id == nextProps.items.item.collection_id) {
                        collTitle = coll.title;

                        tempCollSelection = {
                            value: nextProps.items.item.collection_id,
                            label: collTitle
                        }
                    }
                })
            }


            return {
                collOptions: tempCollOptions,
                collSelection: tempCollSelection,
                updated: true
            }

        }

    }


    handleChange = (newValue) => {
        console.log(newValue);
        if (newValue) {
            this.setState({ 
                newSelection: newValue
            });
        // newValue is not an array if empty
        } 
        // else {
        //     this.setState({ 
        //         collSelection: null
        //     });
        // }
            console.log(this.state.newSelection);

    };

    onClickHandler = () => {

        let updateData = {
            _id: '5edcbc5027c3271205b2360a',
            collection_id: null
        };

        if (this.state.newSelection ) {
            updateData.collection_id = this.state.newSelection.value;

        } 
        console.log(updateData);
        this.props.dispatch(updateItem({
            ...updateData
        }))
    }

    render() {
        console.log(this.state.collSelection);

        const {
            isClearable,
            isSearchable,
            isDisabled,
            isLoading,
            isRtl,
          } = this.state;

        const collSelection = this.state.collSelection;
        const collOptions = this.state.collOptions;


        return (
            <div>
                {this.props.items.item && this.props.items.item.title ?
                    <h2>{this.props.items.item.title}</h2>
                : null }

                <p>Collections:</p>

                {this.state.updated && collOptions ?
                    <Select
                        className="basic-single"
                        classNamePrefix="select"
                        defaultValue={collSelection}
                        isDisabled={isDisabled}
                        isLoading={isLoading}
                        isClearable={isClearable}
                        isRtl={isRtl}
                        isSearchable={isSearchable}
                        name="color"
                        options={collOptions}
                        onChange={this.handleChange}
                    />
                : null}

                <button type="button"onClick={this.onClickHandler}>Submit</button> 

            </div>
        );
    }
};


function mapStateToProps(state) {
    // console.log(state);
    return {
        items:state.items,
        colls:state.collections.colls
    }
}

export default connect(mapStateToProps)(Sandbox);
