import React, { Component } from 'react';
import { connect } from 'react-redux';


import { getIntroText, updateIntroText } from '../../actions';


class AdminIntro extends Component {





    state = {
        introData: {
            title: '',
            body: ''
        }
    }


    componentDidMount() {
        this.props.dispatch(getIntroText());
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.props != prevProps) {


            let introData = {
                title: this.props.text.title,
                body: this.props.text.body
            }



            this.setState({
                introData
            })
        }
    }

    render() {
        return (
            <div>

                {this.props.text && this.props.text.title ?
                    <h1>{this.props.text.title}</h1>
                : null}

                <p>
                    <b>Intro Page</b> 
                </p>

                <textarea
                    type="text"
                    placeholder="Text"
                    defaultValue={null} 
                    onChange={(event) => this.handleInput(event)}
                    rows={6}
                />

                <p>Choose Image</p>
            </div>
        );
    }
}


function mapStateToProps(state) {
    // console.log(state);

    return {
        text: state.intros.text

    }
}


export default connect(mapStateToProps)(AdminIntro)
