import React from 'react';
import Slick from 'react-slick';   // uses cdn css
import { Link } from 'react-router-dom';

import style from './slider.module.css';

const SliderTemplates = (props) => {

  console.log(props.data.list);

  let template = null;



  const settings = {
    dots: false,
    infinite: true,
    arrows: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    ...props.settings
  }

  // const re1 = /.*x/;
  // const re2 = /.*./;
  // const reAll = /.*x.jpg/
  // const re = /^(?!.*test\.js).*\.js$/;
  // console.log(reAll.test("index.jpg"));


  switch(props.type) {
    case 'featured' :

      template = props.data.list.map( (item, i) => {
        return(
          <div key={i}>
            <div className={style.featured_item}>
              <div className={style.featured_image}
                style={{
                  // background: `url(../images/articles/${item.image})`
                  // background: `url(../../../images/sq_thumb/test/item_${item.item_id}.jpg)`
                  background: `url(../../../public/images/sq_thumbs/1.jpg)`

                }}
              >

              </div>
              {/* <Link to={`./articles/${item.item_id}`}> */}
              <Link to={`./items/${item._id}`}>
                <div className={style.featured_caption}>
                  {item.title}

                </div>
              </Link>
            </div>
          </div>
        )
      })


      break;

    // case 'other' :
    //   template = props.something else
    //   break;
    default: 
      template = null;
  }


  return(
    <div id="deleteMe">
    <p>This is the SliderTemplates component (slider_templates.js)</p>
    <Slick {...settings}>
      {template}
    </Slick>
    </div>
  )
}

export default SliderTemplates;
