import React, { Component } from 'react';
import { connect } from 'react-redux';
import moment from 'moment-js';
import { Link } from 'react-router-dom';

// import { getUserPosts } from '../../actions';
import { getUserItems, getAllItems } from '../../actions';



class Sandbox extends Component {


    // componentWillMount() {
    componentDidMount() {
        this.props.dispatch(getUserItems(this.props.user.login.id))
        this.props.dispatch(getAllItems())

    }



    showUserItems = (items) => (
        items.items ?
            items.items.map(item => (
                <tr key={item._id}> 
                    <td>
                        <Link to={`/user/edit-item/${item._id}`}>
                            {/* {item.title} */}

                            { item.omeka ?
                                item.omeka.omeka_id
                            : null}

                        </Link>
                    </td>
                    <td>{item.creator}</td>
                    <td>{moment(item.createAt).format("MM/DD/YY")}</td>
                </tr>
            ))
        : null
    )


    render() {
        // console.log(this.props);

        let items = this.props.items;

        return (
            <div className="user_posts">
                {/* <h4>Your reviews:</h4> */}
                <h4>All items:</h4>

                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Author</th>
                            <th>Date</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.showUserItems(items)}
                    </tbody>
                </table>

            </div>
        );
    }
}

function mapStateToProps(state) {

    console.log(state);
    return {
        user:state.user,
        items:state.items
    }
}


export default connect(mapStateToProps)(Sandbox)
