import React from 'react';
// import {css} from 'glamor';

// const CollsItem = ({item}) => {
const CollListItem = (props) => {

    // let news_item = css({
    //   padding: '20px', 
    //   boxSizing: 'border-box',
    //   borderBottom: '1px solid grey',
    //   ':hover': {
    //     color: 'red'
    //   },
    //   '@media(max-width: 500px)': {
    //     color: 'blue'
    //   }
    // })

    // let item_grey = css({
    //   background: 'lightgrey'
    // })

    const addDefaultSrc = (ev) => {
        const newImg = '/images/default/default.jpg';
        if (ev.target.src != newImg) {
            ev.target.src = newImg
        }  
    } 




    return(

        // INSERT LINK HERE

        // <div className={`${news_item} ${item_grey}`}>
        <div className={'coll_list_item'}>


            <img src={`/images/sq_thumb/${props.item.cover_item}.jpg`} 
                alt={"Item"}
                // onError={"if (this.src != '/images/default/default.jpg') this.src = '/images/default/default.jpg';"} />
                onError={addDefaultSrc} />

            <div className="overlay">
                <h3>{props.item.title}</h3>
            </div>


            {/* <img onError={this.addDefaultSrc} className="img-responsive" src={newsImage} alt={headline}/> */}


            <div>
                <b>Description:</b> {props.item.description}
            </div>
            <div>
                <b>Subject: </b> {props.item.subject}
            </div>
        </div>
    )
}

export default CollListItem;
