import React, { Component } from 'react';
import { connect } from 'react-redux';


import { getColl, searchItem } from '../../actions';
// import { getCollWithItems } from '../../actions';


class Collection  extends Component {



    componentDidMount() {
        let queryId = this.props.match.params.id;

        // let queryKey = this.props.match.params.key;
        // let queryValue = this.props.match.params.value;

        // console.log(queryId);

        this.props.dispatch(getColl(queryId));
        this.props.dispatch(searchItem('collection_id', queryId))
        // this.props.dispatch(getCollWithItems(queryValue));
    }


    renderColl = (coll) => {
      let imageId = null;

      if (coll) {
          imageId = coll[0].cover_item;
      }
      return ( coll ?
        <div>
          <h1>Title</h1> {coll[0].title}
          <h1>Image</h1> <img src={`/images/sq_thumb/1.jpg`} />
          <h1>Description</h1> {coll[0].description}
          <h1>Subject</h1> {coll[0].subject}
          <h1>Format</h1>{coll[0].format}
          <h1>Items</h1>
        </div>
        : null
      )

    }



    renderItems = (items) => (
        items ?
            items.map( (item, i) => (
                `<h2>Title</h2> ${item.title}
                <h2>Creator</h2> ${item.creator}
                <h2>Subject</h2> ${item.subject}
                <h2>Description</h2> ${item.description}`


            ))
        : null

    )







    render() {

        // console.log(this.props.coll);
        // console.log(this.props.items);


        return (

              <div>

                  {this.renderColl(this.props.coll)}
                  { this.props.items ?
                      this.renderItems(this.props.items)
                    : null
                  }





              </div>
        )
    }
}


function mapStateToProps(state) {
    return {
        items: state.items.data,
        coll: state.collections.coll

    }
}


export default connect(mapStateToProps)(Collection)
