import React, { Component } from 'react';
import { connect } from 'react-redux';

import { getItemsByCat, getCats } from '../../actions';




class CatView  extends Component {



    componentDidMount() {

        let catId = this.props.match.params.id

        this.props.dispatch(getItemsByCat(catId));
        this.props.dispatch(getCats());

    }












    render() {

        console.log(this.props.catitems)

        let catitems = this.props.catitems;

        return (

            <div>
                { catitems ?
                    catitems.map( (item, i) => (
                        <p>{item.title}</p>
                     ))
                : null

                }
                Single category view {this.props.match.params.id}





            </div>
        )
    }
}


function mapStateToProps(state) {
    // console.log(state);
    return {
        catitems: state.cats.catitems

    }
}


export default connect(mapStateToProps)(CatView)
