import React from 'react';
import { connect } from 'react-redux';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';


import { getAllColls, getAllCats, getAllSubCats  } from '../../actions';


class Admin extends React.Component  {

    componentDidMount() {
        this.props.dispatch(getAllColls())
        this.props.dispatch(getAllCats());
        this.props.dispatch(getAllSubCats());
    }

    render() {

        console.log(this.props)

        return (
            <div className="main_view admin_view">
                <Tabs>

                    <TabList>
                        <Tab>Categories</Tab>
                        <Tab>Info Page</Tab>
                        <Tab>Intro Page</Tab>
                        <Tab disabled>Toad</Tab>

                    </TabList>

                    <TabPanel>
                        <p>
                            <h2>Categories</h2> 
                        </p>

                        <p><b>Add/Remove Category</b></p>


                        { this.props.cats ?
                            <div>
                                {this.props.cats.map( (cat, i) => (
                                    <p>{cat.title}</p>
                                ))}
                            </div>
                        : null }

                        <p><b>Choose Category Image</b></p>

                        <p><b>Add/Remove Sub-category</b></p>

                        <p><b>Choose Sub-category Image</b></p>

                    </TabPanel>


                    <TabPanel>
                        <p>
                            <b>Info Page</b> 
                        </p>

                        <p>Paragraph 1 Heading</p>
                        <p>Paragraph 1 Content</p>
                        <p>Paragraph 1 Image</p>

                        <p>Paragraph 2 Heading</p>
                        <p>Paragraph 2 Content</p>
                        <p>Paragraph 2 Image</p>

                    </TabPanel>


                    <TabPanel>
                        <p>
                            <b>Intro Page</b> 
                        </p>

                        <p>Text</p>

                        <p>Choose Image</p>
                    </TabPanel>


                    <TabPanel>
                    <p>
                            <b>Toad</b> 
                        </p>
                    </TabPanel>




                </Tabs>
            </div>
        )
    }

}


function mapStateToProps(state) {
    return {
        colls:state.collections.colls,
        cats:state.cats.cats,
        subcats:state.cats.subcats
    }
}

export default connect(mapStateToProps)(Admin)
